import './Timer.css';

const Timer = ()=> {
  //State
  const[seconds, setSeconds] = useState(0);
  const[isActive, setIsActive] = useState(false);

//Toggle the isActive state
  function toggle() {
    setIsActive(!isActive);
  }

//Reset the timer
  function reset() {
  setSeconds(0);
  setIsActive(false);
}

