import React,{useState} from 'react';

/* this is the Trivia Game component. It will contain the list
 of trivia questions and a function to load and return
a question, adding used questions to a list and removing them from
the pool of available questions.
 */
const Trivia = ()=> { 

    const[curQ, setCurQ] = useState(0)
    const[showScore, setShowScore] = useState(false);
    const[score, setScore] = useState(0);
       
    //Trivia game questions
    const questions = [
        {
            questionText: 'what alias does Jeffery Lebowski use?',
			answerOptions: [
				{ answerText: 'Dude', isCorrect: true },
				{ answerText: 'Walter', isCorrect: false },
				{ answerText: 'Marmot', isCorrect: false },
				{ answerText: 'Jackie Treehorn', isCorrect: false },
			],
        },
        {
            questionText: 'what alias does Jeffery Lebowski use?',
			answerOptions: [
				{ answerText: 'Dude', isCorrect: true },
				{ answerText: 'Walter', isCorrect: false },
				{ answerText: 'Marmot', isCorrect: false },
				{ answerText: 'Jackie Treehorn', isCorrect: false },
			],
        },
       {
            q:"Who peed on the dudes rug?",
            pa:["Nihilists","Fascists","Woo","Jackie Treehorn"],
            an: "2",
            resp: "Like I said, WOO peed on my rug",
            imgLink: "https://vignette.wikia.nocookie.net/thebiglebowski/images/a/ab/Woo.jpeg/revision/latest/scale-to-width-down/300?cb=20111216200648"
        },
        {
            q:"Which actor played The Stranger?",
            pa:["Harrison Ford","Sam Davis","Sam Elliot","Nicholas Cage"],
            an: "2",
            resp: "I like yer style Dude",
            imgLink: "https://vignette.wikia.nocookie.net/thebiglebowski/images/5/5b/The_stranger_2.jpg/revision/latest/scale-to-width-down/300?cb=20111216192623"
        },
        {
            q:"What date is written on the check the dude writes to pay for his milk?",
            pa:["Nov 5th","Sept 11th","Oct 31st","April 20th"],
            an: "1",
            resp: "This aggression will not stand",
            imgLink: "https://www.wweek.com/resizer/Aew409Bcr8QmqFfOJj20HXjqAdI=/600x0/filters:quality(100)/s3.amazonaws.com/arc-wordpress-client-uploads/wweek/wp-content/uploads/2016/12/20124140/screener_The-Big-Lebowski_check_Working-Title-Films-PolyGram-Filmed-Entertainment_4308.jpg"
        },
        {
            q:"What is the clean version of the infamous 'Do you see what happens larry?' scene",
            pa:["Find and store an apple","Spend a summer in Atlanta","Fill a stein and then ask","Find a stranger in the Alps"],
            an: "3",
            resp: "This aggression will not stand",
            imgLink: "https://i.ytimg.com/vi/6YZ_vBoBoLo/hqdefault.jpg"
        },  
    ];

    const handleAnswerButtonClick = (isCorrect) => {

        if(isCorrect === true){
            setScore(score + 1);
            }
        const nextQuestion = curQ+1;
        if(nextQuestion < questions.length){
            setCurQ(nextQuestion);	
        } else {
            setShowScore(true);
            }
    };
    
    return (
        <div className="trivia">
           {showScore ? (
				<div className='score-section'>You scored {score} out of {questions.length}</div>
			) : (
				<>
					<div className='question-section'>
						<div className='question-count'>
							<span>Question {curQ + 1} </span>/{questions.length}
						</div>
						<div className='question-text'>{questions[curQ].q}</div>
					</div>
					<div className='answer-section'>
                        {questions[curQ].pa.map((answerOption)=> 
                            <button onClick={()=> handleAnswerButtonClick(answerOption.isCorrect)}>{answerOption.answerText}</button>)
                        }
					
					
					</div>
				</>
			)}
        </div>
      );
      

};

export default Trivia;
